export class KanbanComponent {
    constructor(route, cheetaTicketService, cheetaUserService, cheetaTagsService, dialog) {
        this.route = route;
        this.cheetaTicketService = cheetaTicketService;
        this.cheetaUserService = cheetaUserService;
        this.cheetaTagsService = cheetaTagsService;
        this.dialog = dialog;
        this.backlog = [];
        this.development = [];
        this.testing = [];
        this.done = [];
        this.deployed = [];
        //filters option variables
        this.projectName = "";
