        this.membersArr = {
            developers: [],
            testers: [],
            others: []
        };
        this.toShow = {
            'backlog': true,
            'development': true,
            'testing': true,
            'done': true,
            'deployed': true,
        };
        this.isLoading = true;
    }
    async ngOnInit() {
        this.isLoading = true;
        this.route.queryParamMap.subscribe(async (paramMap) => {
            if (paramMap.has("search")) {
                // Use search instead of get
                await this.searchTicket(paramMap.get('search'));
            }
