export class LoginComponent {
    constructor(authService, router) {
        this.authService = authService;
        this.router = router;
        this.email = "";
        this.password = "";
        this.showPassword = false;
        this.emailValid = true;
        this.passValid = true;
        this.emailTitle = false;
        this.passTitle = false;
    }
    ngOnInit() {
        this.authService.autoAuthUser();
        let auth = this.authService.getAuthStatus();
        if (auth) {
            this.router.navigate(['/']);
        }
    }
    async onLogin() {
        if (true) { // (this.email != '' && this.password != '' && this.emailValid && this.passValid) {
            let loginForm = {
                email: this.email,
                password: this.password
            };
            await this.authService.login(loginForm);
        }
    }
    getInputType() {
        if (this.showPassword) {
            return 'text';
        }
        return 'password';
    }
    toggleShowPassword() {
        this.showPassword = !this.showPassword;
    }
    onEmailKeyUp() {
        this.emailTitle = true;
        var mailformat = /^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/;
        if (this.email.match(mailformat))
            this.emailValid = true;
        else
            this.emailValid = false;
    }
    onPassKeyUp() {
        this.passTitle = true;
        if (this.password) {
            if (this.password.length > 20 ||
                this.password.length < 6)
                this.passValid = false;
            else
                this.passValid = true;
        }
        // console.log(this.password.length);
    }
}
