export class KanbanComponent {
    constructor(route, cheetaTicketService, cheetaUserService, cheetaTagsService, dialog, notification) {
        this.route = route;
        this.cheetaTicketService = cheetaTicketService;
        this.cheetaUserService = cheetaUserService;
        this.cheetaTagsService = cheetaTagsService;
        this.dialog = dialog;
        this.notification = notification;
        this.backlog = [];
        this.development = [];
        this.testing = [];
        this.done = [];
        this.deployed = [];
        //filters option variables
        this.projectName = "";
        this.members = "";
        this.priority = "";
        this.projectNameArr = [];
        this.membersArr = [];
        this.isLoading = true;
    }
    async ngOnInit() {
        this.isLoading = true;
        this.route.queryParamMap.subscribe(async (paramMap) => {
            // console.log(paramMap)
            if (paramMap.has("search")) {
                // console.log("Search : ", paramMap.get('search'));
                // Use search instead of get
                await this.searchTicket(paramMap.get('search'));
            }
            else
                await this.loadPrevFilters();
        });
        let resMembers = await this.cheetaUserService.getUsersList();
        if (resMembers && resMembers.length)
            this.membersArr = resMembers;
        let resProjects = await this.cheetaTagsService.getProjectsList();
        if (resProjects && resProjects.length)
            this.projectNameArr = resProjects;
        this.takeSub = this.cheetaTicketService.getTicketUpdateListener()
            .subscribe(async (data) => {
            await this.onApply();
        });
    }
    async drop(event) {
        // console.log('event ::',event)
        if (event.previousContainer === event.container) {
            moveItemInArray(event.container.data, event.previousIndex, event.currentIndex);
        }
        else {
            const t = this.transferValid(event.previousContainer.id, event.container.id);
            if (!t) {
                this.notification.openWarningDialog('Not valid Movement');
                return;
            }
            transferArrayItem(event.previousContainer.data, event.container.data, event.previousIndex, event.currentIndex);
            this.autoAddUser(event.container.data[event.currentIndex]);
            this.updateTicketStatus(event.container.data[event.currentIndex], {
                prev: event.previousContainer.id,
                new: event.container.id,
                prevData: event.previousContainer.data,
                newData: event.container.data,
                prevIndex: event.previousIndex,
                newIndex: event.currentIndex
            });
        }
    }
    transferValid(prevState, newState) {
        const stateType = ['backlog', 'development', 'testing', 'done', 'deployed'];
        if (stateType.indexOf(newState) > stateType.indexOf(prevState)
            && stateType.indexOf(newState) == stateType.indexOf(prevState) + 1) {
            // console.log(ticket)
            return true;
        }
        if (stateType.indexOf(newState) < stateType.indexOf(prevState)
            && newState == "backlog")
            return true;
        return false;
    }
    async searchTicket(search) {
        let result = await this.cheetaTicketService.searchTicket(search);
        if (result) {
            this.loadTickets(result);
            let arr = [];
            if (localStorage.getItem('recentSearch')) {
                let a = localStorage.getItem('recentSearch');
                arr = [...JSON.parse(a)];
                let check = arr.filter(i => { if (i == search)
                    return i; });
                if (!(check.length > 0)) {
                    arr.unshift(search);
                    if (arr.length > 5)
                        arr.pop();
                    localStorage.setItem('recentSearch', JSON.stringify(arr));
                }
                return;
            }
            arr.unshift(search);
            localStorage.setItem('recentSearch', JSON.stringify(arr));
        }
    }
    // it will work on two conditions
    // * onInIt
    // * using filters
    async getTickets(filters) {
        let result = await this.cheetaTicketService.getTickets(filters);
        if (result) {
            this.loadTickets(result);
        }
    }
    loadTickets(data) {
        this.backlog = [];
        this.development = [];
        this.testing = [];
        this.deployed = [];
        this.done = [];
        data.forEach((i) => {
            if (i.state == 'backlog')
                this.backlog.unshift(i);
            if (i.state == 'development')
                this.development.unshift(i);
            if (i.state == 'testing')
                this.testing.unshift(i);
            if (i.state == 'deployed')
                this.deployed.unshift(i);
            if (i.state == 'done')
                this.done.unshift(i);
        });
        this.isLoading = false;
    }
    async loadPrevFilters() {
        if (localStorage.getItem('lastFilter') &&
            localStorage.getItem('lastFilter') !== "{}") {
            let filterOption = "{}";
            filterOption = localStorage.getItem('lastFilter');
            filterOption = JSON.parse(filterOption);
            await this.getTickets(filterOption);
            // add filters to filters drop down
            if (filterOption['members'])
                this.members = filterOption['members'];
            if (filterOption['priority'])
                this.priority = filterOption['priority'];
            if (filterOption['project'])
                this.projectName = filterOption['project'];
        }
        else {
            await this.getTickets({});
        }
    }
    async updateTicketStatus(ticket, states) {
        const changes = {
            states: {
                prev: states.prev,
                new: states.new
            },
            user: await this.getUserDets()
        };
        try {
            let result = await this.cheetaTicketService.updateTicketState(ticket, changes);
            console.log(result);
        }
        catch (err) {
            transferArrayItem(states.newData, states.prevData, states.newIndex, states.prevIndex);
            this.notification.openWarningDialog('' + err);
            return;
        }
    }
    async onApply() {
        this.isLoading = true;
        const filterOption = {};
        if (this.members)
            filterOption['members'] = [this.members];
        if (this.priority)
            filterOption['priority'] = [this.priority];
        if (this.projectName)
            filterOption['project'] = [this.projectName];
        await this.getTickets(filterOption);
        localStorage.setItem('lastFilter', JSON.stringify(filterOption));
    }
    async autoAddUser(ticket) {
        let flag = ticket.members.filter((m) => {
            if (m.userId == localStorage.getItem('userId'))
                return m;
        });
        if (!flag.length) {
            let members = [...ticket.members, await this.getUserDets()];
            await this.cheetaTicketService.updateTicket(ticket._id, { members });
        }
    }
    async getUserDets() {
        let userDets = await this.cheetaUserService.getUserData();
        if (userDets.data)
            return {
                email: userDets.data.name,
                type: userDets.data.type,
                userId: userDets.data._id,
                profilePic: userDets.data.profilePic ? userDets.data.profilePic : ''
            };
        return {};
    }
    ngOnDestroy() {
        this.takeSub.unsubscribe();
