import { __decorate } from "tslib";
import { Component } from '@angular/core';
import { moveItemInArray, transferArrayItem } from '@angular/cdk/drag-drop';
let KanbanComponent = class KanbanComponent {
    constructor(cheetaTicketService, cheetaUserService, cheetaTagsService) {
        this.cheetaTicketService = cheetaTicketService;
        this.cheetaUserService = cheetaUserService;
        this.cheetaTagsService = cheetaTagsService;
        this.backlog = [];
        this.development = [];
        this.testing = [];
        this.done = [];
        this.deployed = [];
        //filters option variables
        this.projectName = "";
        this.members = "";
        this.priority = "";
        this.projectNameArr = [];
        this.membersArr = [];
        this.isLoading = true;
    }
    async ngOnInit() {
        this.isLoading = true;
        await this.getTickets({});
        let resMembers = await this.cheetaUserService.getUsersList();
        if (resMembers && resMembers.length)
            this.membersArr = resMembers;
        let resProjects = await this.cheetaTagsService.getProjectsList();
        if (resProjects && resProjects.length)
            this.projectNameArr = resProjects;
        this.takeSub = this.cheetaTicketService.getTicketUpdateListener()
            .subscribe(async (data) => {
            await this.getTickets({});
        });
        // this.isLoading= false;
    }
    drop(event) {
        // console.log('event ::',event)
        if (event.previousContainer === event.container) {
            moveItemInArray(event.container.data, event.previousIndex, event.currentIndex);
        }
        else {
            transferArrayItem(event.previousContainer.data, event.container.data, event.previousIndex, event.currentIndex);
            this.updateTicketStatus(event.container.data[event.currentIndex], event.container.id);
        }
    }
    // it will work on two conditions
    // * onInIt
    // * using filters
    async getTickets(filters) {
        let result = await this.cheetaTicketService.getTickets(filters);
        if (result) {
            console.log("this loaadding", this.isLoading);
            this.isLoading = false;
        }
        this.backlog = [];
        this.development = [];
        this.testing = [];
        this.deployed = [];
        this.done = [];
        // console.log(result)
        result.forEach((i) => {
            if (i.state == 'backlog')
                this.backlog.push(i);
            if (i.state == 'development')
                this.development.push(i);
            if (i.state == 'testing')
                this.testing.push(i);
            if (i.state == 'deployed')
                this.deployed.push(i);
            if (i.state == 'done')
                this.done.push(i);
        });
    }
    async updateTicketStatus(ticket, state) {
        // console.log('ticket :', ticket)
        // console.log('to state :', state)
        let result = await this.cheetaTicketService.updateTicketState(ticket, state);
    }
    async onApply() {
        const filterOption = {};
        if (this.members)
            filterOption['members'] = [this.members];
        if (this.priority)
            filterOption['priority'] = this.priority;
        if (this.projectName)
            filterOption['project'] = [this.projectName];
        await this.getTickets(filterOption);
    }
    ngOnDestroy() {
        this.takeSub.unsubscribe();
    }
};
