import { Subject } from "rxjs";
import { environment } from "src/environments/environment";
import { NotificationsComponent } from "../notifications/notifications.component";
import * as i0 from "@angular/core";
import * as i1 from "@angular/common/http";
import * as i2 from "@angular/router";
import * as i3 from "@angular/material/dialog";
const usersURL = environment.cheetaUserURL;
export class AuthService {
    constructor(http, router, dialog) {
        this.http = http;
        this.router = router;
        this.dialog = dialog;
        this.token = null;
        this.tokenTimer = null;
        this.authStatusListener = new Subject();
        this.authStatus = false;
    }
    getToken() {
        return this.token;
    }
    getAuthStatusListener() {
        return this.authStatusListener.asObservable();
    }
    getAuthStatus() {
        return this.authStatus;
    }
    register(regData) {
        console.log("api url", usersURL);
        this.http
            .post(usersURL, regData)
            .subscribe((resp) => {
            console.log("lolz ::: new resopnse", resp);
            console.log("userId::", resp.data.userId);
            if (resp && resp.status && resp.status.code == 200) {
                this.router.navigate(['/auth/verifyOTP'], { queryParams: { email: resp.data.email, userId: resp.data.userId } });
            }
        }, error => {
            console.log(error);
        });
    }
    verifyOtp(otpData) {
        console.log('verfying otp', otpData);
        this.http
            .post(usersURL + "/verifyOTP", otpData)
            .subscribe((resp) => {
            console.log("lolz ::: new resopnse", resp);
            if (resp && resp.status && resp.status.code == 200)
                this.openDialog(' Successfully');
            this.router.navigate(['/auth/login']);
        }, error => {
            console.log(error);
        });
    }
    autoAuthUser() {
        const authInfo = this.getAuthData();
        if (authInfo) {
            let expiresIn = new Date(authInfo.expirationDate).getTime() - (new Date()).getTime();
            if (expiresIn > 0) {
                this.token = authInfo.token;
                this.authStatus = true;
                this.tokenTimer = setTimeout(() => {
                    this.logout();
                }, expiresIn);
                this.authStatusListener.next(true);
            }
        }
    }
    login(form) {
        console.log("api url :: ", usersURL);
        this.http
            .post(usersURL + 'login', form)
            .subscribe(async (resp) => {
            if (resp) {
                console.log('login res ::', resp);
                this.token = resp.data.token;
                if (this.token != null) {
                    let expirationDate = this.getExpirationDate(resp.data.expiresIn);
                    this.saveAuthData(resp.data, expirationDate);
                    this.tokenTimer = setTimeout(() => {
                        this.logout();
                    }, resp.data.expiresIn * 1000);
                    this.authStatusListener.next(true);
                    this.authStatus = true;
                    this.router.navigate(['']);
                }
            }
        }, err => {
            console.log(err);
            // console.log("geetaaa", err);
            // this.authStatusListener.next(false);
        });
    }
    logout() {
        this.token = null;
        this.authStatus = false;
        this.authStatusListener.next(false);
        clearTimeout(this.tokenTimer);
        localStorage.clear();
        this.router.navigate(['/auth/login']);
    }
    saveAuthData(obj, expirationDate) {
        localStorage.setItem('expirationDate', expirationDate.toISOString());
        localStorage.setItem('mobile', obj.mobile);
        localStorage.setItem('token', obj.token);
        localStorage.setItem('email', obj.email);
        localStorage.setItem('userId', obj.userId);
    }
    getAuthData() {
        if (localStorage.getItem("token") &&
            localStorage.getItem("expirationDate"))
            return {
                token: localStorage.getItem("token"),
                expirationDate: localStorage.getItem("expirationDate")
            };
        return false;
    }
    getExpirationDate(expiresIn) {
        let now = new Date();
        return new Date(now.getTime() + expiresIn * 1000);
    }
    resendOtp(email) {
        console.log(email);
        return new Promise((resolve, reject) => {
            try {
                this.http
                    .post(usersURL + '/forgetPassword', { email })
                    .subscribe((resp) => {
                    console.log("resp of send otp for pass forget", resp);
                    if (resp && resp.status.code == 200) {
                        console.log('OTP sent');
                    }
                    resolve(resp);
                }, error => {
                    console.log(error);
                });
            }
            catch (err) {
                console.log("inside catch err", err);
                reject(err);
            }
        });
    }
    changePassword(data) {
        console.log("data", data);
        return new Promise((resolve, reject) => {
            try {
                this.http
                    .post(usersURL + '/newPassword', data)
                    .subscribe((resp) => {
                    console.log(resp);
                    console.log(resp.status);
                    if (resp && resp['status'] && resp['status'].code == 200) {
                        this.router.navigate(['/auth/login']);
                    }
                }, error => {
                    console.log(error);
                });
            }
            catch (err) {
                console.log("inside catch err of resetPass", err);
                reject(err);
            }
        });
    }
    openDialog(warning) {
        const dialogRef = this.dialog.open(NotificationsComponent, {
            data: warning,
        });
    }
}
