export class UserProfileComponent {
    constructor(cheetaUsersService, fileUploader, dialog, notification) {
        this.cheetaUsersService = cheetaUsersService;
        this.fileUploader = fileUploader;
        this.dialog = dialog;
        this.notification = notification;
        //variable names taken for edit
        this.userProfile = {
            name: "",
            mobile: '',
            email: '',
            profilePic: '',
            type: '',
            telegram: {
                id: '',
                username: ''
            }
        };
        this.roleTypeArr = environment.roleTypeArray;
        this.isLoading = false;
    }
    async ngOnInit() {
        this.isLoading = true;
        const res = await this.cheetaUsersService.getUserData();
        await this.updateUi(res.data);
        this.isLoading = false;
    }
    //filepicker function for updating profile pic
    async onFilePicked(e) {
        this.isLoading = true;
        let ele = e.target;
        const FILE = ele.files[0];
