import { __decorate } from "tslib";
import { Component } from '@angular/core';
let HomeComponent = class HomeComponent {
    constructor(dialog, cheetaTicketService, cheetaUsersService) {
        this.dialog = dialog;
        this.cheetaTicketService = cheetaTicketService;
        this.cheetaUsersService = cheetaUsersService;
        this.bigTask = [];
        this.smallTask = [];
        this.score = 0;
        this.topPerformers = [];
        this.ticketSummary = [];
        this.days = [
            'Sunday', 'Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday'
        ];
        this.isLoading = true;
    }
    async ngOnInit() {
        this.isLoading = true;
        await this.getDate();
        // get users ticket/tasks
        await this.getUsersTickets();
        // get scores
        await this.getScores();
        // get project wise ticket summary
        await this.getTicketSummary();
        this.isLoading = false;
        this.takeSub = this.cheetaTicketService.getTicketUpdateListener()
            .subscribe(async (data) => {
            await this.getUsersTickets();
            await this.getTicketSummary();
            await this.getScores();
        });
    }
    async getDate() {
        this.date = new Date();
        this.day = this.date.getDay();
    }
    async getUsersTickets() {
        this.smallTask = [];
        this.bigTask = [];
        const filters = {
            members: [localStorage.getItem('userId')]
        };
        let result = await this.cheetaTicketService.getTickets(filters);
        const userDetails = await this.cheetaUsersService.getUserData();
        this.userName = userDetails.data.name;
        let userType = "";
        if (userDetails)
            userType = userDetails.data.type;
        else
            return;
        if (userType == 'software-developer' ||
            userType == 'tech-trainee' ||
            userType == 'team-leader') {
            result.forEach((t) => {
                if (t.state != "development")
                    return;
                if (t.type == 'small')
                    this.smallTask.push(t);
                if (t.type == 'big')
                    this.bigTask.push(t);
            });
            return;
        }
        if (userType == 'quality-analyst' ||
            userType == 'qa-trainee' ||
            userType == 'project-manager' ||
            userType == 'devOps') {
            result.forEach((t) => {
                if (t.state != "testing")
                    return;
                if (t.type == 'small')
                    this.smallTask.push(t);
                if (t.type == 'big')
                    this.bigTask.push(t);
            });
            return;
        }
        if (userType == 'marketing/sales') {
            result.forEach((t) => {
                if (!(t.state == "done" || t.state == "deployed"))
                    return;
                if (t.type == 'small')
                    this.smallTask.push(t);
                if (t.type == 'big')
                    this.bigTask.push(t);
            });
            return;
        }
    }
    async getScores() {
        let result = await this.cheetaUsersService.getScore();
        this.score = result.userScore;
        this.topPerformers = [...result.topPerformers];
    }
    async getTicketSummary() {
        let result = await this.cheetaTicketService.getTicketSummary();
        this.ticketSummary = result;
    }
    ngOnDestroy() {
        this.takeSub.unsubscribe();
    }
};
HomeComponent = __decorate([
    Component({
        selector: 'app-home',
        templateUrl: './home.component.html',
        styleUrls: ['./home.component.scss']
    })
], HomeComponent);
export { HomeComponent };
