import { __decorate, __param } from "tslib";
import { Component, Inject } from '@angular/core';
import { MAT_DIALOG_DATA } from '@angular/material/dialog';
import { environment } from 'src/environments/environment';
import { CreateValidation } from '../validations/validation-instance';
let CreateTicketComponent = class CreateTicketComponent {
    constructor(dialogRef, cheetaTicketService, cheetaUserService, data) {
        this.dialogRef = dialogRef;
        this.cheetaTicketService = cheetaTicketService;
        this.cheetaUserService = cheetaUserService;
        this.data = data;
        //ngModel names used for form
        this.createTicket = {
            title: '',
            description: '',
            projectName: '',
            priority: '',
            type: '',
            files: [],
            expectedTime: '',
            UT: false,
            documentation: false,
            testDoneBy: '',
            tags: [],
            projectType: '',
            msg: '',
            user: '',
        };
        this.members = [];
        //for validation
        this.titleValidation = new CreateValidation();
        this.descriptionValidation = new CreateValidation();
        this.priorityValidation = new CreateValidation();
        //for array list from environments
        this.projectNameArr = environment.projectNameArray;
        this.membersArr = [];
        this.addOn = false;
        this.advanceOn = false;
        this.labelPosition = "before";
        this.edit = false;
    }
    async ngOnInit() {
        let res = await this.cheetaUserService.getUsersList();
        if (res && res.length)
            this.membersArr = res;
        // console.log(this.membersArr)
        if (this.data.edit) {
            this.createTicket = Object.assign({}, this.data.ticketInfo);
        }
        console.log(this.data);
    }
    async onCreate() {
        const ticket = {
            title: this.createTicket.title,
            description: this.createTicket.description,
            priority: this.createTicket.priority,
            // projectName: this.createTicket.projectName,
            // tags: [
            //   this.createTicket.projectType + this.createTicket.projectName
            // ],
            // type: this.createTicket.type,
            // files: this.createTicket.files,
            // members: this.members,
            // comments: [
            //   {
            //     user: this.createTicket.user,
            //     msg: this.createTicket.msg
            //   }
            // ],
            // UT : this.createTicket.UT,
            // documentation : this.createTicket.documentation,
            // expectedTime: this.createTicket.expectedTime,
        };
        console.log("ticket info ::", ticket);
        if (ticket.title && ticket.description && ticket.priority) {
            let res;
            if (!this.data.edit)
                res = await this.cheetaTicketService.addTicket(ticket);
            else
                res = await this.cheetaTicketService.updateTicket(this.data.ticketInfo._id, ticket);
            // Add a toaster for Success msg and failure msgs accordingly
            // in place of alerts!!!!!!!!
            if (res)
                alert("Created Successfully!");
            this.dialogRef.close();
        }
        else {
            alert('Invalid Details!');
            return;
        }
    }
    addMember(ele) {
        let element = ele.target;
        let flag = 0;
        this.members.forEach(e => {
            if (e.userId == element.value)
                flag++;
        });
        if (flag)
            return;
        this.membersArr.forEach(e => {
            if (e._id == element.value)
                this.members.push({
                    name: e.name,
                    email: e.email,
                    userId: e._id,
                    type: e.type
                });
        });
        console.log(this.members);
    }
    remove(member) {
        const index = this.members.indexOf(member);
        if (index >= 0) {
            this.members.splice(index, 1);
        }
    }
    onValidation() {
        this.titleValidation.errorMessage = "This field is required";
        this.descriptionValidation.errorMessage = "This field is required";
        this.priorityValidation.errorMessage = "this field is required";
    }
};
