import { __decorate, __param } from "tslib";
import { Component, Inject, ViewChild } from '@angular/core';
import { MAT_DIALOG_DATA } from '@angular/material/dialog';
let CreateTicketComponent = class CreateTicketComponent {
    constructor(dialogRef, cheetaTicketService, cheetaUserService, cheetaTagsService, fileUploader, dialog, notification, data) {
        this.dialogRef = dialogRef;
        this.cheetaTicketService = cheetaTicketService;
        this.cheetaUserService = cheetaUserService;
        this.cheetaTagsService = cheetaTagsService;
        this.fileUploader = fileUploader;
        this.dialog = dialog;
        this.notification = notification;
        this.data = data;
        this.projectNameArr = [];
        //ngModel names used for form
        this.createTicket = {
            title: '',
            description: '',
            priority: '',
            files: [],
            tags: [],
            members: [],
            comments: [],
            type: '',
            expectedTime: '',
            UT: 'false',
            documentation: 'false',
        };
        this.membersArr = {
            developers: [],
            testers: [],
            others: []
        };
        this.addOn = false;
        this.advanceOn = false;
        this.labelPosition = "before";
        this.edit = false;
        this.btnString = "Create";
        this.isClicked = false;
        this.isLoading = false;
        this.min = new Date();
    }
    async ngOnInit() {
        // this.isLoading=true;
        let resProjects = await this.cheetaTagsService.getProjectsList();
        if (resProjects && resProjects.length)
            this.loadTags(resProjects);
        await this.membersCategory();
        if (this.data.edit) {
            this.isLoading = true;
            this.createTicket = Object.assign({}, this.data.ticketInfo);
            // if(this.data.ticketInfo.tags) this.createTicket.tags = this.data.ticketInfo.tags.join(', ')
            if (this.data.ticketInfo.UT)
                this.createTicket.UT = 'true';
            else
                this.createTicket.UT = 'false';
            if (this.data.ticketInfo.documentation)
                this.createTicket.documentation = 'true';
            else
                this.createTicket.documentation = 'false';
            this.btnString = 'Save Changes';
            this.isLoading = false;
        }
        this.isLoading = false;
    }
    async onCreate() {
        const ticket = {
            title: this.createTicket.title,
            description: this.createTicket.description,
            priority: this.createTicket.priority,
            type: this.createTicket.type
        };
        // if(this.createTicket.type) ticket['type'] = this.createTicket.type;
        if (this.createTicket.UT == 'true')
            ticket['UT'] = true;
        else
            ticket['UT'] = false;
        if (this.createTicket.documentation == 'true')
            ticket['documentation'] = true;
        else
            ticket['documentation'] = false;
        if (this.createTicket.expectedTime)
            ticket['expectedTime'] = this.createTicket.expectedTime;
        if (this.createTicket.members) {
            ticket['members'] = [...this.createTicket.members];
        }
        if (this.createTicket.comments)
            ticket['comments'] = [...this.createTicket.comments];
        if (this.createTicket.files)
            ticket['files'] = [...this.createTicket.files];
        if (this.createTicket.tags)
            ticket['tags'] = [...this.createTicket.tags];
        // ticket['tags'] = this.createTicket.tags.replace(/\s+/g, '').split(',');
        if (ticket.title && ticket.description && ticket.priority && ticket.type) {
            let res;
            this.isClicked = true;
            if (!this.data.edit) {
                res = await this.cheetaTicketService.addTicket(ticket);
                this.dialogRef.close();
            }
            else {
                res = await this.cheetaTicketService.updateTicket(this.data.ticketInfo._id, ticket);
                this.dialogRef.close();
            }
            // Add a toaster for Success msg and failure msgs accordingly
            // in place of alerts!!!!!!!!
            if (res) {
                this.notification.openDialog(this.btnString + ' Successfully');
                this.dialogRef.close();
            }
        }
        else if (ticket.title == '') {
            this.notification.openWarningDialog('Title is Required!');
            return;
        }
        else if (ticket.description == '') {
            this.notification.openWarningDialog('Description is Required!');
            return;
        }
        else if (ticket.priority == '') {
            this.notification.openWarningDialog('Priority is Required!');
            return;
        }
        else if (ticket.type == '') {
            this.notification.openWarningDialog('Type is Required!');
            return;
        }
    }
    async membersCategory() {
        let resMembers = await this.cheetaUserService.getUsersList();
        resMembers.forEach((a) => {
            if (a.type == 'software-developer')
                this.membersArr.developers.push(a);
            else if (a.type == 'quality-analyst')
                this.membersArr.testers.push(a);
            else
                this.membersArr.others.push(a);
        });
    }
    addMember(ele) {
        let element = ele.target;
        let flag = 0;
        this.createTicket.members.forEach((e) => {
            if (e.userId == element.value)
                flag++;
        });
        if (flag)
            return;
        this.membersArr.developers.forEach((e) => {
            if (e._id == element.value)
                this.createTicket.members.push({
                    name: e.name,
                    email: e.email,
                    userId: e._id,
                    type: e.type,
                    profilePic: e.profilePic,
                });
        });
        this.membersArr.testers.forEach((e) => {
            if (e._id == element.value)
                this.createTicket.members.push({
                    name: e.name,
                    email: e.email,
                    userId: e._id,
                    type: e.type,
                    profilePic: e.profilePic,
                });
        });
        this.membersArr.others.forEach((e) => {
            if (e._id == element.value)
                this.createTicket.members.push({
                    name: e.name,
                    email: e.email,
                    userId: e._id,
                    type: e.type,
                    profilePic: e.profilePic,
                });
        });
    }
    remove(member) {
        const index = this.createTicket.members.indexOf(member);
        if (index >= 0) {
            this.createTicket.members.splice(index, 1);
        }
    }
    removeFiles(files) {
        const index = this.createTicket.files.indexOf(files);
        if (index >= 0) {
            this.createTicket.files.splice(index, 1);
        }
    }
    addComment(comment) {
        let commentObj = {
            user: localStorage.getItem('email'),
            msg: comment.value,
            dateTime: new Date().toISOString()
        };
        this.createTicket.comments.push(commentObj);
    }
    async selectedFile(e) {
        let ele = e.target;
        const FILE = ele.files[0];
        const fileUrl = await this.fileUploader.uploadFile('cheetaFiles', FILE);
        this.createTicket.files.push(fileUrl);
        this.file.nativeElement.value = "";
    }
    addTags(event) {
        let ele = event.target;
        if (!ele.value)
            return;
        const value = ele.value.trim();
        this.createTicket.tags.push(value);
    }
    removeTags(event) {
        const index = this.createTicket.tags.indexOf(event);
        if (index >= 0) {
            this.createTicket.tags.splice(index, 1);
        }
    }
    loadTags(data) {
        for (let i = 0; i < data.length; i++) {
            this.projectNameArr.push('P:' + data[i]);
            this.projectNameArr.push('B:' + data[i]);
            this.projectNameArr.push('F:' + data[i]);
        }
    }
    goToLink(url) {
        window.open(url, "_blank");
    }
};
