import { environment } from 'src/environments/environment';
import * as i0 from "@angular/core";
import * as i1 from "@angular/common/http";
import * as i2 from "@angular/router";
const TICKET_BASE_URL = environment.ticketUrl;
export class CheetaTicketService {
    constructor(http, router) {
        this.http = http;
        this.router = router;
        this.smallTaskArr = [];
        this.bigTaskArr = [];
        //function used in home page forshowing all ticketsummary table
        this.ticketSummary = [];
        this.cacheTime = 5000;
    }
    //this ir to create a ticket from create option
    addTicket(ticket) {
        return new Promise((resolve, reject) => {
            try {
                this.http
                    .post(TICKET_BASE_URL + 'createTicket', ticket)
                    .subscribe(async (res) => { resolve(res); });
            }
            catch (err) {
            }
        });
    }
    //this is for getting all tickets from backend to kan ban page
    getTickets(filters) {
        return new Promise((resolve, reject) => {
            try {
                this.http
                    .post(TICKET_BASE_URL + "getTickets", filters)
                    .subscribe(async (res) => {
                    if (res.data && res.data.length)
                        resolve(res.data);
                    else
                        resolve([]);
                });
            }
            catch (err) {
                console.log(err);
            }
        });
    }
    getTicketSummary() {
        return new Promise((resolve, reject) => {
            try {
                if (this.ticketSummary.length > 0)
                    resolve(this.ticketSummary);
                this.http
                    .get(TICKET_BASE_URL + 'getTicketSummary')
                    .subscribe((res) => {
                    if (res && res['data']) {
                        // resolve(res);
                        let t;
                        t = res['data'];
                        for (let i = 0; i < t.length; i++) {
                            t[i].P0 = 0;
                            t[i].P1 = 0;
                            t[i].P2 = 0;
                            t[i].P3 = 0;
                            t[i].P4 = 0;
                            for (let j = 0; j < t[i].ticketsCount.length; j++) {
                                // if(t[i].ticketsCount[j].priority == "P0")
                                t[i][t[i].ticketsCount[j].priority] = t[i].ticketsCount[j].count;
                            }
                        }
                        this.ticketSummary = t;
                        setTimeout(() => { this.ticketSummary = []; }, this.cacheTime);
                        return resolve(this.ticketSummary);
                    }
                    reject(new Error("data is not there"));
                }, error => {
                    reject(error);
                });
            }
            catch (err) {
                console.log(err);
            }
        });
    }
    //changes state of a ticket in kanban saction from backlog to development or deployment or done
    async updateTicketState(ticket, state) {
        return new Promise((resolve, reject) => {
            try {
                this.http
                    .put(TICKET_BASE_URL + 'changeTicketState/' + ticket._id, { state })
                    .subscribe((res) => {
                    // console.log(res);
                    if (res)
                        resolve(res);
                });
            }
            catch (err) {
                console.log(err);
            }
        });
    }
    //function to delete tickets
    deleteTicket() {
        return new Promise((resolve, reject) => {
            try {
                this.http
                    .delete(TICKET_BASE_URL + localStorage.getItem('userId'))
                    .subscribe(async (res) => {
                    resolve(res);
                });
            }
            catch (err) {
                reject(err);
            }
        });
    }
    //function to update ticket details
    updateTicket(id, ticket) {
        return new Promise((resolve, reject) => {
            this.http.
                put(TICKET_BASE_URL + 'updateTicket/' + id, ticket)
                .subscribe(async (res) => {
                resolve(res);
            }, (err) => {
                reject(err);
            });
        });
    }
    getSmallTaskSummary() {
        // console.log("in service")
        return new Promise((resolve, reject) => {
            try {
                this.http
                    .get(TICKET_BASE_URL + 'getTicketsByMember?userId=' + localStorage.getItem('userId'))
                    .subscribe((res) => {
                    // console.log(res)
                });
            }
            catch (err) {
                console.log(err);
            }
        });
    }
    getOkr() {
        // console.log("in service");
    }
}
